    "series":[""],
    "data": [[]],
    "labels": [""]
}];

let templateHumid = [{
  "series": [""],
  "data": [[]],
  "labels": [""]
}];

let templateLux = 0;

get(child(dbRef, `product_data/${pid}`)).
then((datapoints) => {
  if (datapoints.exists()) {
    const data = datapoints.val();

    let maxKey = '0.1';
    let keys =  Object.keys(data);
    for (let i = 0;i < keys.length;i++){
      let key = keys[i];
      if(parseInt(key) > parseInt(maxKey)){
        maxKey = key;
      }

      templateTemp[0]["data"][0].push({
        "x" : parseInt(key), "y" : data[key][0]["data"]
      })
      templateHumid[0]["data"][0].push({
        "x": parseInt(key), "y": data[key][1]["data"]
      })

    }
    templateLux = maxKey;
    flow.set("dataTemp",templateTemp);
    flow.set("dataHumid",templateHumid);
    flow.set("dataLux",data[templateLux][2]["data"] * 1.0);
    console.log(templateTemp);

  } else {
    console.log("No data available");
  }
}).catch((error) => {
  console.error(error);
});


return msg;

