let auth = flow.get("auth");
firebaseAuth.signOut(auth)
    .then((userCredentials) => 
    {   
        global.set("user", null);
        global.set("dataTopic", null);
        global.set("gasTopic", null);
        global.set("commandTopic", null);
        global.set("error", "none");
        global.set("state",0);
        flow.set("signout","Signed out");        
    })
    .catch((error) => {
        flow.set("signout", "Error trying to sign out");
    });
